import { useEffect, useState } from "react";

import { MenuNav } from "./MenuNav";
import { menuItemArray } from "./fauxMenu-TEMP";
import "./MenuPage.css";

const MenuPage = () => {
  const [isFlip, setIsFlip] = useState(false);
  // const test = () => {
  //     console.log(menuItemArray[0].image)
  // }

  const flipCard = (e) => {
    e.stopPropagation();
    setIsFlip(!isFlip)
  };

  const frontCardItem = (item) => {
    return (
      <div className="card">
        <div
          className="menu-item front-card"
          key={`item-${item.id}`}
        >
          <div>{item.name}</div>
          <img src={item.image} />
        </div>
        {/* <button>Add to cart</button> */}
      </div>
    )
  }

  const backCardItem = (item) => {
    return (
      <div className="card">
        <div
          className="menu-item back-card"
          key={`item-${item.id}`}
        >
          <div>{item.name}</div>
          <div>{item.ingredients}</div>
          {/* <div>{item.nutrition_table}</div> */}
        </div>
        {/* <button>Add to cart</button> */}
      </div>
    )
  }
  //add an onload feature
  return (
    <div className="menu">
      <div className="headers">OUR MENU</div>
      <MenuNav />
      <div className="menu-item-container">
        {menuItemArray.map((item) => {
          return (
            <div onClick={flipCard}>
              {isFlip ? backCardItem(item) : frontCardItem(item)}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MenuPage;
